import "./App.css";
import {
  Grid,
  Box,
  Typography,
  Button,
  CardMedia,
  CardContent,
  CardActions,
  Card,
} from "@mui/material";
// import event1 from "./images/event1.jpg";

import * as React from "react";
import EventModal from "./eventModal";
import { mockEvents } from "./mockEvents";

function Events() {
  const [visibleCardId, setVisibleCardId] = React.useState("");

  return (
    <Box padding={4}>
      <Box padding={2}>
        <Typography variant="h6">Events this week</Typography>
      </Box>
      <Grid container spacing={2}>
        {mockEvents.map((mockEvent) => (
          <>
            <Grid item xs={3}>
              <Card sx={{ height: 300 }}>
                <CardMedia
                  sx={{ height: 140 }}
                  image={mockEvent.image}
                  title="green iguana"
                />
                <CardContent>
                  <Typography gutterBottom variant="h5" component="div">
                    {mockEvent.name}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {mockEvent.location}
                  </Typography>
                </CardContent>
                <Box width="inherit">
                  <CardActions>
                    <Button
                      size="small"
                      onClick={() => setVisibleCardId(mockEvent.id)}
                    >
                      Learn More
                    </Button>
                  </CardActions>
                </Box>
              </Card>
            </Grid>
            <EventModal
              description={mockEvent.description}
              open={visibleCardId === mockEvent.id}
              setVisibleCardId={setVisibleCardId}
              name={mockEvent.name}
              date={mockEvent.date}
              image={mockEvent.image}
            />
          </>
        ))}
        <Grid item xs={3}>
          <Card sx={{ maxWidth: 345 }}>
            <CardMedia
              sx={{ height: 140 }}
              image={require("./images/avocado.png")}
              title="green iguana"
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                New
              </Typography>
              <Typography variant="body2" color="text.secondary">
                New
              </Typography>
            </CardContent>
            <CardActions>
              <Button size="small">Create a new event</Button>
            </CardActions>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
}

export default Events;
