import "./App.css";

import {
  Grid,
  Box,
  Typography,
  Button,
  CardMedia,
  CardContent,
  CardActions,
  Card,
} from "@mui/material";

import * as React from "react";
import EventModal from "./eventModal";

const mockEvents = [
  {
    id: "1",
    name: "event1",
    description: "my fun event",
    imagePath: "./images/avocado.png",
    location: "Surry Hills",
  },
  {
    id: "2",
    name: "event2",
    description: "my cool event",
    imagePath: "./images/avocado.png",
    location: "Glebe",
  },
  {
    id: "3",
    name: "event3",
    description: "my event",
    imagePath: "./images/avocado.png",
    location: "Office",
  },
  {
    id: "4",
    name: "event4",
    description: "fun event",
    imagePath: "./images/avocado.png",
    location: "Townhall",
  },
  {
    id: "5",
    name: "event5",
    description: "very fun event",
    imagePath: "./images/avocado.png",
    location: "Coogee",
  },
];

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  bgcolor: "background.paper",
  border: "2px solid #000",
  boxShadow: 24,
  p: 4,
};

function Events() {
  const [visible, setVisible] = React.useState(false);

  return (
    <Box padding={4}>
      <Box padding={2}>
        <Typography variant="h6">Events this week</Typography>
      </Box>
      <Grid container spacing={2}>
        {mockEvents.map((mockEvent) => (
          <>
            <Grid item xs={3}>
              <Card sx={{ maxWidth: 345 }}>
                <CardMedia
                  sx={{ height: 140 }}
                  image={require("./images/avocado.png")}
                  title="green iguana"
                />
                <CardContent>
                  <Typography gutterBottom variant="h5" component="div">
                    {mockEvent.name}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {mockEvent.location}
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button size="small" onClick={setVisible(true)}>
                    Learn More
                  </Button>
                </CardActions>
              </Card>
            </Grid>
            <EventModal
              description={mockEvent.description}
              visible={visibleCardId === mockEvent.id}
            />
          </>
        ))}
        <Grid item xs={3}>
          <Card sx={{ maxWidth: 345 }}>
            <CardMedia
              sx={{ height: 140 }}
              image={require("./images/avocado.png")}
              title="green iguana"
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                New
              </Typography>
              <Typography variant="body2" color="text.secondary">
                New
              </Typography>
            </CardContent>
            <CardActions>
              <Button size="small">Create a new event</Button>
            </CardActions>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
}

export default Events;
